import React, {useState, useEffect} from 'react'

import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import Layout from '../components/Layout.js'
import XSPFilters from '../components/XSPFilters.js';
import LauchList from '../components/LaunchList/LaunchList';

function HomePage({data}) {
  let initialState = [{
        type: "launch_year",
        displayName: "Launch Year",
        data: new Array((new Date().getFullYear() - 2005)).fill().map((a, i) => 2006 + i),
        activeItem: false
    },
    {
        type: "launch_success",
        displayName: "Successful Launch",
        data: ["true", "false"],
        activeItem: false
    },
    {
        type: "land_success",
        displayName: "Successful Landing",
        data: ["true", "false"],
        activeItem: false
    }]

    const [filterState, setFilterState] = useState(initialState);
    const [spaceXData, setSpaceXData] = useState([]);

    useEffect(() => {
      setSpaceXData(data);
    }, [0])

    let launchList = <div></div>;

    launchList = <LauchList launchData={spaceXData} />

    function updateQueryStringParameter(uri, key, value) {
      var re = new RegExp("([?&])" + key + "=.*?(&|$)", "i");
      var separator = uri.indexOf('?') !== -1 ? "&" : "?";
      if (uri.match(re)) {
        return uri.replace(re, '$1' + key + "=" + value + '$2');
      }
      else {
        return uri + separator + key + "=" + value;
      }
    }

    const onFilterApply = (category, value) => {
      var queryString = updateQueryStringParameter(location.search, category, value);
      window.history.pushState("", "", queryString);
      fetch(`https://api.spaceXdata.com/v3/launches${queryString}&limit=100`).then(response => response.json()).then(
        (data) => {
          setSpaceXData(data)

          if(category === 'launch_year'){

          }
          const updatedArray = filterState.map(a => {
            if (a.type == 'launch_year' || a.type === 'launch_success' || a.type === 'land_success') {
               a.activeItem = value;
            }
            return a;
         });

          setFilterState(updatedArray);
        }
      );
    }

  return <>
  <Header />
    <Layout>
      <XSPFilters filterData={filterState} onFilterApply={onFilterApply} />
      {launchList}
    </Layout>
  <Footer />
  </>
}

export async function getServerSideProps({query}) {
  const queryString = require('query-string');
  const res = await fetch(`https://api.spaceXdata.com/v3/launches?${queryString.stringify(query)}&limit=100`)
  const data = await res.json()
  return {
    props: {
      data
    },
  }
}

export default HomePage
