import React, {useState, useEffect} from 'react'
import { createBrowserHistory } from "history";

import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import Layout from '../components/Layout.js'
import XSPFilters from '../components/XSPFilters.js';
import LauchList from '../components/LaunchList/LaunchList';
import { getParam, setParam } from '../helper/urlManipulation.js';

function HomePage({posts}) {
  let initialState = [{
        type: "launch_year",
        displayName: "Launch Year",
        data: new Array((new Date().getFullYear() - 2005)).fill().map((a, i) => 2006 + i),
    },
    {
        type: "launch_success",
        displayName: "Successful Launch",
        data: ["true", "false"],
    },
    {
        type: "land_success",
        displayName: "Successful Landing",
        data: ["true", "false"],
    }]

    const [filterState, setFilterState] = useState(initialState);
    const [spaceXData, setSpaceXData] = useState([]);
    const [urlQuery, setUrlQuery] = useState();
    const [initialUrl, setInitialUrl] = useState()

    const [pValues, setPValues] = useState();
    const queryString = require('query-string');

    useEffect(() => {
      setSpaceXData(posts);
    }, [0])

    let launchList = <div></div>;

    launchList = <LauchList launchData={spaceXData} />

    function updateQueryStringParameter(uri, key, value) {
      var re = new RegExp("([?&])" + key + "=.*?(&|$)", "i");
      var separator = uri.indexOf('?') !== -1 ? "&" : "?";
      if (uri.match(re)) {
        return uri.replace(re, '$1' + key + "=" + value + '$2');
      }
      else {
        return uri + separator + key + "=" + value;
      }
    }

    const onFilterApply = (category, value) => {

      var newUrl=updateQueryStringParameter(window.location.href,"some_param","replaceValue");
      console.log('newUrl', newUrl)

      let detectValues = {}
      
      let parsedValues = queryString.parse(location.search)

      parsedValues[category] = value;
     // setPValues(parsedValues);

      //const returnedTarget = Object.assign(pValues, parsedValues);
      //parsedValues.category = category;
      //parsedValues.launch_year = '2009';

      //const stringified = queryString.stringify(parsed);

      //location.search = stringified;
      //console.log(stringified);
      //let currentParams = location.search;
      //console.log(parsedValues)
    }

    useEffect(() => {
      fetch(`https://api.spaceXdata.com/v3/launches?limit=100&${urlQuery}`).then(response => response.json()).then(data => setSpaceXData(data));
    }, [urlQuery])

    useEffect(() => {
      setInitialUrl(window.location.href)
    }, [0])

  return <>
  <Header />
    <Layout>
      <XSPFilters filterData={filterState} onFilterApply={onFilterApply} />
      {launchList}
    </Layout>
  <Footer />
  </>
}

export async function getStaticProps() {
  const res = await fetch('https://api.spaceXdata.com/v3/launches?limit=100')
  const posts = await res.json()
  return {
    props: {
      posts,
    },
  }
}

export default HomePage
