import React, {useState, useEffect} from 'react'
import { useHistory } from "react-router-dom";
import { useRouter } from 'next/router'

import Header from '../components/Header.js'
import Footer from '../components/Footer.js'
import Layout from '../components/Layout.js'
import XSPFilters from '../components/XSPFilters.js';
import LauchList from '../components/LaunchList/LaunchList';
import { getParam, setParam } from '../helper/urlManipulation.js';

function HomePage({posts}) {
  const queryString1 = require('query-string');
  const history = useHistory();
  const router = useRouter()
  let initialState = [{
        type: "launch_year",
        displayName: "Launch Year",
        data: new Array((new Date().getFullYear() - 2005)).fill().map((a, i) => 2006 + i),
        //activeItem: this.props.query["launch_year"] && parseInt(this.props.query["launch_year"])
    },
    {
        type: "launch_success",
        displayName: "Successful Launch",
        data: ["true", "false"],
        //activeItem: this.props.query["launch_success"]
    },
    {
        type: "land_success",
        displayName: "Successful Landing",
        data: ["true", "false"],
        //activeItem: this.props.query["land_success"]
    }]

    const [filterState, setFilterState] = useState(initialState);
    const [spaceXData, setSpaceXData] = useState([]);
    const [urlQuery, setUrlQuery] = useState();

    const currentQueries = getParam();

    function updateQueryStringParameter(uri, key, value) {
      var re = new RegExp("([?&])" + key + "=.*?(&|$)", "i");
      var separator = uri.indexOf('?') !== -1 ? "&" : "?";
      if (uri.match(re)) {
        return uri.replace(re, '$1' + key + "=" + value + '$2');
      }
      else {
        return uri + separator + key + "=" + value;
      }
    }

    useEffect(() => {
      setSpaceXData(posts);
    }, [0])

    let launchList = <div></div>;

    launchList = <LauchList
                    launchData={spaceXData}
                    // dataLimit={this.state.dataLimit}
                    // fetchMoreData={this.fetchMoreData}
                />

    const onFilterApply = (category, value) => {

    const currentQueries = [];
    currentQueries[category] = value;
    const queryString = setParam(currentQueries);
    router.push("?" + queryString)
    setUrlQuery(queryString);
    updateQueryStringParameter(category, value);
    setUrlQuery(queryString);

    console.log("searchwa", location.search);

      // if (error) return <div>failed to load</div>
      // if (!data) return <div>loading...</div>
      // return <div>hello {data.name}!</div>
      // const currentQueries = getParam(this.state.queryString);
      // const currentCategory = { ...this.state.filterData.find(filterCategory => filterCategory.type === category) };

      // if (currentCategory.activeItem && currentCategory.activeItem === value) {
      //     currentQueries[category] = null;
      //     currentCategory.activeItem = null;
      // }
      // else {
      //     currentCategory.activeItem = value;
      //     currentQueries[category] = value;
      // }
      // const queryString = setParam(currentQueries);

      // this.props.history.push("?" + queryString);
      // this.setState(prevState => {
      //     return {
      //         filterData: prevState.filterData.map(filterCategory => filterCategory.type === category ? currentCategory : filterCategory),
      //         queryString: queryString
      //     }
      // })
  }

  useEffect(() => {
    fetch(`https://api.spaceXdata.com/v3/launches?${urlQuery}`).then(response => response.json()).then(data => setSpaceXData(data));
    console.log('query', `https://api.spaceXdata.com/v3/launches?${urlQuery}`)
  }, [urlQuery])

  return <>
  <Header />
    <Layout>
      <XSPFilters filterData={filterState} onFilterApply={onFilterApply} />
      {launchList}
    </Layout>
  <Footer />
  </>
}

export async function getStaticProps() {
  // Call an external API endpoint to get posts.
  // You can use any data fetching library
  const res = await fetch('https://api.spaceXdata.com/v3/launches?limit=100')
  const posts = await res.json()

  // By returning { props: posts }, the Blog component
  // will receive `posts` as a prop at build time
  return {
    props: {
      posts,
    },
  }
}

export default HomePage
